mapper.insert:
insert into user
    (id, username, email, age)
values (#{user.id}, #{user.username}, #{user.email}, #{user.age});

mapper.update:
update user
    set username = #{user.username},
        email = #{user.email},
        age = #{user.age}
where id = #{id}

mapper.select:
select id, username, email, age
from user
where id = #{id};
